import React, { useState } from 'react';

function MealPlannerForm() {
  const [form, setForm] = useState({
    diet: '',
    goal: '',
    mealsPerDay: 3,
    planLength: 7,
    budget: '',
    allergies: ''
  });

  const [loading, setLoading] = useState(false);
  const [mealPlan, setMealPlan] = useState('');
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMealPlan('');
    setError('');

    const prompt = `
You are a meal planner AI. Create a ${form.planLength}-day meal plan with ${form.mealsPerDay} meals per day.
Dietary preference: ${form.diet || 'None'}
Goal: ${form.goal}
Budget level: ${form.budget}
Allergies: ${form.allergies || 'None'}

Each day should include:
- Meal names and short descriptions
- Simple bullet-point cooking steps
- Ingredient reuse across days to save money

At the end, generate a grocery list grouped by category (Produce, Grains, Protein, Spices, Other).
`;

    try {
      const response = await fetch("http://localhost:5000/api/mealplan", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({ prompt })
      });

      const data = await response.json();
      if (data.result) {
        setMealPlan(data.result);
      } else {
        setError("Something went wrong. Please try again or check your server.");
      }
    } catch (err) {
      console.error(err);
      setError("Error connecting to backend.");
    }

    setLoading(false);
  };

  const handleDownload = () => {
    const element = document.createElement("a");
    const file = new Blob([mealPlan], { type: "text/plain" });
    element.href = URL.createObjectURL(file);
    element.download = "meal-plan.txt";
    document.body.appendChild(element);
    element.click();
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Dietary Preference:</label>
          <select name="diet" value={form.diet} onChange={handleChange}>
            <option value="">None</option>
            <option value="vegetarian">Vegetarian</option>
            <option value="vegan">Vegan</option>
            <option value="gluten-free">Gluten-Free</option>
            <option value="low-carb">Low-Carb</option>
            <option value="high-protein">High-Protein</option>
          </select>
        </div>

        <div className="form-group">
          <label>Meal Planning Goal:</label>
          <select name="goal" value={form.goal} onChange={handleChange}>
            <option value="">Select a goal</option>
            <option value="healthy">Eat healthier</option>
            <option value="budget">Save time & money</option>
            <option value="simple">Just plan meals</option>
          </select>
        </div>

        <div className="form-group">
          <label>Meals Per Day:</label>
          <input type="number" name="mealsPerDay" min="1" max="3" value={form.mealsPerDay} onChange={handleChange} />
        </div>

        <div className="form-group">
          <label>Plan Length (days):</label>
          <select name="planLength" value={form.planLength} onChange={handleChange}>
            <option value={3}>3</option>
            <option value={5}>5</option>
            <option value={7}>7</option>
          </select>
        </div>

        <div className="form-group">
          <label>Budget Level:</label>
          <select name="budget" value={form.budget} onChange={handleChange}>
            <option value="">Select</option>
            <option value="low">Low</option>
            <option value="medium">Medium</option>
            <option value="high">High</option>
          </select>
        </div>

        <div className="form-group">
          <label>Allergies:</label>
          <input type="text" name="allergies" value={form.allergies} onChange={handleChange} placeholder="e.g., peanuts, dairy" />
        </div>

        <button type="submit">{loading ? "Generating..." : "Generate Meal Plan"}</button>
      </form>

      {error && <p style={{ color: 'red', marginTop: '20px' }}>{error}</p>}

      {mealPlan && (
        <div style={{ marginTop: '30px' }}>
          <h3>Generated Meal Plan:</h3>
          <pre style={{ whiteSpace: 'pre-wrap', background: '#f9fafb', padding: '16px', borderRadius: '8px' }}>{mealPlan}</pre>
          <button onClick={handleDownload}>Download as .txt</button>
        </div>
      )}
    </div>
  );
}

export default MealPlannerForm;
