import React, { useState } from 'react';

const MEAL_STYLES = [
  "Vegan", "Vegetarian", "Pescatarian", "Keto",
  "Paleo", "Mediterranean", "High-Protein",
  "Low-Carb", "Low-Fat", "Whole30"
];

const MEAL_ORDER = ["Breakfast", "Lunch", "Dinner"];

function MealPlannerForm() {
  const [form, setForm] = useState({
    mealStyles: [],
    goal: '',
    mealsPerDay: 3,
    planLength: 7,
    budget: '',
    allergies: ''
  });

  const [loading, setLoading] = useState(false);
  const [mealPlan, setMealPlan] = useState('');
  const [error, setError] = useState('');
  const [parsedPlan, setParsedPlan] = useState({ days: [], groceryList: {} });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (name === "mealStyles") {
      setForm((prev) => ({
        ...prev,
        mealStyles: checked
          ? [...prev.mealStyles, value]
          : prev.mealStyles.filter((v) => v !== value)
      }));
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const cleanLine = (line) =>
    line.replace(/\*+/g, '')         // remove asterisks
        .replace(/：/g, ':')           // full-width colon to normal
        .replace(/^[-•–]\s*/, '')    // remove bullets
        .trim();

  const parseMealPlan = (text) => {
    const lines = text.split('\n').map(cleanLine).filter(Boolean);
    const days = [];
    const groceryList = {};

    let currentDay = null;
    let currentMeal = null;
    let currentGroceryCategory = '';
    let inGrocery = false;
    const dayRegex = /^Day\s+\d+:?/i;
    const mealRegex = /^(Breakfast|Lunch|Dinner):/i;

    for (let line of lines) {
      if (dayRegex.test(line)) {
        if (currentDay) days.push(currentDay);
        currentDay = { title: line.replace(':', ''), meals: [] };
        inGrocery = false;
      } else if (mealRegex.test(line)) {
        const [type, ...rest] = line.split(':');
        currentMeal = { type: type.trim(), title: rest.join(':').trim(), steps: [] };
        if (currentDay) currentDay.meals.push(currentMeal);
      } else if ((line.startsWith('-') || line.startsWith('•') || line.startsWith('–')) && currentMeal) {
        currentMeal.steps.push(cleanLine(line));
      } else if (line.toLowerCase().includes('grocery list')) {
        if (currentDay) days.push(currentDay);
        currentDay = null;
        inGrocery = true;
      } else if (inGrocery && line.endsWith(':')) {
        currentGroceryCategory = line.replace(':', '');
        groceryList[currentGroceryCategory] = [];
      } else if (inGrocery && (line.startsWith('-') || line.startsWith('•') || line.startsWith('–'))) {
        const item = cleanLine(line);
        if (!currentGroceryCategory) currentGroceryCategory = 'Uncategorized';
        if (!groceryList[currentGroceryCategory]) groceryList[currentGroceryCategory] = [];
        groceryList[currentGroceryCategory].push(item);
      }
    }

    if (currentDay) days.push(currentDay);

    const trimmedDays = days.slice(0, Number(form.planLength)).map(day => {
      const validMeals = MEAL_ORDER.map(type =>
        day.meals.find(m => m.type.toLowerCase() === type.toLowerCase())
      ).filter(Boolean);
      return { ...day, meals: validMeals.slice(0, form.mealsPerDay) };
    });

    return { days: trimmedDays, groceryList };
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMealPlan('');
    setError('');
    setParsedPlan({ days: [], groceryList: {} });

    const prompt = `
You are a meal planner AI. Generate a ${form.planLength}-day meal plan with exactly ${form.mealsPerDay} meals per day.
Meal styles: ${form.mealStyles.length ? form.mealStyles.join(', ') : 'None'}
Goal: ${form.goal}, Budget: ${form.budget}, Allergies: ${form.allergies || 'None'}

Format:
Day 1:
Breakfast: [title]
- step
Lunch: [title]
- step

At the end, include:
GROCERY LIST:
Produce:
- item
Grains:
- item
Protein:
- item
Spices:
- item
Other:
- item
`;

    try {
      const response = await fetch("http://localhost:5000/api/mealplan", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt })
      });

      const data = await response.json();
      if (data.result) {
        console.log("RAW:", data.result);
        setMealPlan(data.result);
        const parsed = parseMealPlan(data.result);
        setParsedPlan(parsed);
      } else {
        setError("Something went wrong. Please try again.");
      }
    } catch (err) {
      console.error(err);
      setError("Could not connect to backend.");
    }
    setLoading(false);
  };

  const handleDownload = () => {
    const element = document.createElement("a");
    const file = new Blob([mealPlan], { type: "text/plain" });
    element.href = URL.createObjectURL(file);
    element.download = "meal-plan.txt";
    document.body.appendChild(element);
    element.click();
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Meal Style Preferences:</label>
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
            {MEAL_STYLES.map(style => (
              <label key={style}>
                <input
                  type="checkbox"
                  name="mealStyles"
                  value={style}
                  checked={form.mealStyles.includes(style)}
                  onChange={handleChange}
                /> {style}
              </label>
            ))}
          </div>
        </div>

        <label>Meal Planning Goal:</label>
        <select name="goal" value={form.goal} onChange={handleChange}>
          <option value="">Select a goal</option>
          <option value="healthy">Eat healthier</option>
          <option value="budget">Save time & money</option>
          <option value="simple">Just plan meals</option>
        </select>

        <label>Meals Per Day:</label>
        <input type="number" name="mealsPerDay" min="1" max="3" value={form.mealsPerDay} onChange={handleChange} />

        <label>Plan Length (days):</label>
        <select name="planLength" value={form.planLength} onChange={handleChange}>
          {[3, 5, 7].map(num => <option key={num} value={num}>{num}</option>)}
        </select>

        <label>Budget Level:</label>
        <select name="budget" value={form.budget} onChange={handleChange}>
          <option value="">Select</option>
          <option value="low">Low</option>
          <option value="medium">Medium</option>
          <option value="high">High</option>
        </select>

        <label>Allergies:</label>
        <input type="text" name="allergies" value={form.allergies} onChange={handleChange} />

        <button type="submit">{loading ? "Generating..." : "Generate Meal Plan"}</button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {parsedPlan.days.length > 0 && (
        <div style={{ marginTop: '30px' }}>
          <h3>Weekly Meal Plan:</h3>
          {parsedPlan.days.map((day, index) => (
            <div key={index} style={{ padding: '16px', background: '#f9fafb', borderRadius: '12px', marginBottom: '12px' }}>
              <h4>{day.title}</h4>
              {day.meals.map((meal, i) => (
                <div key={i}>
                  <strong>{meal.type}: {meal.title}</strong>
                  <ul>
                    {meal.steps.map((step, j) => (
                      <li key={j}>{step}</li>
                    ))}
                  </ul>
                </div>
              ))}
            </div>
          ))}

          <h3>Grocery List:</h3>
          {Object.keys(parsedPlan.groceryList).length > 0 ? (
            <div style={{ background: '#f3f4f6', borderRadius: '12px', padding: '16px' }}>
              {Object.keys(parsedPlan.groceryList).map((category, idx) => (
                <div key={idx}>
                  <strong>{category}</strong>
                  <ul>
                    {parsedPlan.groceryList[category].map((item, i) => <li key={i}>{item}</li>)}
                  </ul>
                </div>
              ))}
            </div>
          ) : (
            <p><em>No grocery list was provided.</em></p>
          )}
          <button onClick={handleDownload}>Download as .txt</button>
        </div>
      )}
    </div>
  );
}

export default MealPlannerForm;
