import React, { useState } from 'react';

function MealPlannerForm() {
  const [form, setForm] = useState({
    diet: '',
    goal: '',
    mealsPerDay: 3,
    planLength: 7,
    budget: '',
    allergies: ''
  });

  const [loading, setLoading] = useState(false);
  const [plan, setPlan] = useState('');
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setPlan('');

    const prompt = generatePrompt(form);

    try {
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`
        },
        body: JSON.stringify({
          model: 'gpt-3.5-turbo',
          messages: [{ role: 'user', content: prompt }],
          temperature: 0.7
        })
      });

      const data = await response.json();
      if (data.choices && data.choices.length > 0) {
        setPlan(data.choices[0].message.content.trim());
      } else {
        throw new Error('Invalid response from AI');
      }
    } catch (err) {
      console.error(err);
      setError('Something went wrong. Please try again or check your API key.');
    } finally {
      setLoading(false);
    }
  };

  const generatePrompt = (form) => {
    return `Create a ${form.planLength}-day meal plan with ${form.mealsPerDay} meals per day.
Diet: ${form.diet || 'None'}.
Goal: ${form.goal}.
Budget level: ${form.budget}.
Allergies to avoid: ${form.allergies || 'None'}.

The plan should:
- Be organized by Day 1, Day 2, etc.
- List each meal with a short description
- Include cooking instructions in bullet steps
- Reuse ingredients when possible to save money
- Include a categorized grocery list at the end
`;
  };

  const handleDownload = () => {
    const blob = new Blob([plan], { type: 'text/plain' });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = 'meal-plan.txt';
    link.click();
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Dietary Preference:</label>
          <select name="diet" value={form.diet} onChange={handleChange}>
            <option value="">None</option>
            <option value="vegetarian">Vegetarian</option>
            <option value="vegan">Vegan</option>
            <option value="gluten-free">Gluten-Free</option>
            <option value="low-carb">Low-Carb</option>
            <option value="high-protein">High-Protein</option>
          </select>
        </div>

        <div className="form-group">
          <label>Meal Planning Goal:</label>
          <select name="goal" value={form.goal} onChange={handleChange}>
            <option value="">Select a goal</option>
            <option value="healthy">Eat healthier</option>
            <option value="budget">Save time & money</option>
            <option value="simple">Just plan meals</option>
          </select>
        </div>

        <div className="form-group">
          <label>Meals Per Day:</label>
          <input type="number" name="mealsPerDay" min="1" max="3" value={form.mealsPerDay} onChange={handleChange} />
        </div>

        <div className="form-group">
          <label>Plan Length (days):</label>
          <select name="planLength" value={form.planLength} onChange={handleChange}>
            <option value={3}>3</option>
            <option value={5}>5</option>
            <option value={7}>7</option>
          </select>
        </div>

        <div className="form-group">
          <label>Budget Level:</label>
          <select name="budget" value={form.budget} onChange={handleChange}>
            <option value="">Select</option>
            <option value="low">Low</option>
            <option value="medium">Medium</option>
            <option value="high">High</option>
          </select>
        </div>

        <div className="form-group">
          <label>Allergies:</label>
          <input type="text" name="allergies" value={form.allergies} onChange={handleChange} placeholder="e.g., peanuts, dairy" />
        </div>

        <button type="submit" disabled={loading}>
          {loading ? 'Generating...' : 'Generate Meal Plan'}
        </button>
      </form>

      {error && <p style={{ color: 'red', marginTop: '20px' }}>{error}</p>}
      {plan && (
        <div style={{ whiteSpace: 'pre-wrap', marginTop: '30px' }}>
          <h3>Your AI-Generated Meal Plan</h3>
          <button onClick={handleDownload} style={{ marginBottom: '20px' }}>
            Save as .TXT
          </button>
          <div>{plan}</div>
        </div>
      )}
    </>
  );
}

export default MealPlannerForm;
