import React, { useState } from 'react';

const MEAL_STYLES = [
  "Vegan", "Vegetarian", "Pescatarian", "Keto",
  "Paleo", "Mediterranean", "High-Protein",
  "Low-Carb", "Low-Fat", "Whole30"
];

function MealPlannerForm() {
  const [form, setForm] = useState({
    mealStyles: [],
    goal: '',
    mealsPerDay: 3,
    planLength: 7,
    budget: '',
    allergies: ''
  });

  const [loading, setLoading] = useState(false);
  const [mealPlan, setMealPlan] = useState('');
  const [error, setError] = useState('');
  const [parsedPlan, setParsedPlan] = useState({ days: [], groceryList: {} });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (name === "mealStyles") {
      setForm((prev) => ({
        ...prev,
        mealStyles: checked
          ? [...prev.mealStyles, value]
          : prev.mealStyles.filter((v) => v !== value)
      }));
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const parseMealPlan = (text) => {
    const days = [];
    const groceryList = {};
    const lines = text.split('\n').map(line => line.trim()).filter(Boolean);

    let currentDay = null;
    let dayTitles = new Set();
    let currentMealType = '';
    let inGrocery = false;
    let currentGroceryCategory = '';

    lines.forEach(line => {
      if (line.match(/^Day \d+:?/)) {
        if (currentDay && !dayTitles.has(currentDay.title)) {
          days.push(currentDay);
          dayTitles.add(currentDay.title);
        }
        currentDay = { title: line.replace(':', ''), meals: [] };
      } else if (line.match(/^(Breakfast|Lunch|Dinner):/)) {
        currentMealType = line.split(':')[0];
        if (currentDay) {
          currentDay.meals.push({ type: currentMealType, title: line.split(': ')[1], steps: [] });
        }
      } else if (line.startsWith('-') && !inGrocery) {
        if (currentDay && currentDay.meals.length > 0) {
          const currentMeal = currentDay.meals[currentDay.meals.length - 1];
          if (currentMeal.steps) {
            currentMeal.steps.push(line.replace('- ', ''));
          }
        }
      } else if (line === 'Grocery List:') {
        if (currentDay && !dayTitles.has(currentDay.title)) {
          days.push(currentDay);
          dayTitles.add(currentDay.title);
        }
        inGrocery = true;
      } else if (inGrocery && line.endsWith(':')) {
        currentGroceryCategory = line.replace(':', '');
        groceryList[currentGroceryCategory] = [];
      } else if (inGrocery && line.startsWith('-')) {
        groceryList[currentGroceryCategory]?.push(line.replace('- ', ''));
      }
    });

    if (currentDay && !dayTitles.has(currentDay.title)) {
      days.push(currentDay);
    }

    // Truncate to requested number of days
    return {
      days: days.slice(0, Number(form.planLength)),
      groceryList
    };
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMealPlan('');
    setError('');
    setParsedPlan({ days: [], groceryList: {} });

    const prompt = `
You are a meal planner AI. Create a ${form.planLength}-day meal plan with ${form.mealsPerDay} meals per day.
Meal style preferences: ${form.mealStyles.length ? form.mealStyles.join(', ') : 'None'}
Goal: ${form.goal}
Budget level: ${form.budget}
Allergies: ${form.allergies || 'None'}

Follow this structure exactly:
- Label each day with "Day 1:", "Day 2:", etc.
- Do not repeat any day label
- Provide exactly ${form.planLength} days
- For each day, include only ${form.mealsPerDay} meal(s) in order using these labels: Breakfast, Lunch, Dinner (skip unused)
- Include short bullet-point cooking steps under each meal
- Reuse ingredients across days

At the end, generate a grocery list grouped by category (Produce, Grains, Protein, Spices, Other).
`;

    try {
      const response = await fetch("http://localhost:5000/api/mealplan", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt })
      });

      const data = await response.json();
      if (data.result) {
        setMealPlan(data.result);
        const parsed = parseMealPlan(data.result);
        setParsedPlan(parsed);
      } else {
        setError("Something went wrong. Please try again or check your server.");
      }
    } catch (err) {
      console.error(err);
      setError("Error connecting to backend.");
    }

    setLoading(false);
  };

  const handleDownload = () => {
    const element = document.createElement("a");
    const file = new Blob([mealPlan], { type: "text/plain" });
    element.href = URL.createObjectURL(file);
    element.download = "meal-plan.txt";
    document.body.appendChild(element);
    element.click();
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Meal Style Preferences:</label>
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
            {MEAL_STYLES.map((style) => (
              <label key={style} style={{ display: 'flex', alignItems: 'center', gap: '6px' }}>
                <input
                  type="checkbox"
                  name="mealStyles"
                  value={style}
                  checked={form.mealStyles.includes(style)}
                  onChange={handleChange}
                />
                {style}
              </label>
            ))}
          </div>
        </div>

        <div className="form-group">
          <label>Meal Planning Goal:</label>
          <select name="goal" value={form.goal} onChange={handleChange}>
            <option value="">Select a goal</option>
            <option value="healthy">Eat healthier</option>
            <option value="budget">Save time & money</option>
            <option value="simple">Just plan meals</option>
          </select>
        </div>

        <div className="form-group">
          <label>Meals Per Day:</label>
          <input type="number" name="mealsPerDay" min="1" max="3" value={form.mealsPerDay} onChange={handleChange} />
        </div>

        <div className="form-group">
          <label>Plan Length (days):</label>
          <select name="planLength" value={form.planLength} onChange={handleChange}>
            <option value={3}>3</option>
            <option value={5}>5</option>
            <option value={7}>7</option>
          </select>
        </div>

        <div className="form-group">
          <label>Budget Level:</label>
          <select name="budget" value={form.budget} onChange={handleChange}>
            <option value="">Select</option>
            <option value="low">Low</option>
            <option value="medium">Medium</option>
            <option value="high">High</option>
          </select>
        </div>

        <div className="form-group">
          <label>Allergies:</label>
          <input type="text" name="allergies" value={form.allergies} onChange={handleChange} placeholder="e.g., peanuts, dairy" />
        </div>

        <button type="submit">{loading ? "Generating..." : "Generate Meal Plan"}</button>
      </form>

      {error && <p style={{ color: 'red', marginTop: '20px' }}>{error}</p>}

      {parsedPlan.days.length > 0 && (
        <div style={{ marginTop: '30px' }}>
          <h3>Weekly Meal Plan:</h3>
          {parsedPlan.days.map((day, index) => (
            <div key={index} style={{ marginBottom: '20px', padding: '16px', background: '#f9fafb', borderRadius: '12px' }}>
              <h4>{day.title}</h4>
              {day.meals.map((meal, i) => (
                <div key={i}>
                  <strong>{meal.type}: {meal.title}</strong>
                  <ul>
                    {meal.steps && meal.steps.map((step, j) => (
                      <li key={j}>{step}</li>
                    ))}
                  </ul>
                </div>
              ))}
            </div>
          ))}

          <h3>Grocery List:</h3>
          <div style={{ background: '#f3f4f6', borderRadius: '12px', padding: '16px' }}>
            {Object.keys(parsedPlan.groceryList).map((category, idx) => (
              <div key={idx} style={{ marginBottom: '16px' }}>
                <strong>{category}</strong>
                <ul>
                  {parsedPlan.groceryList[category].map((item, i) => (
                    <li key={i}>{item}</li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
          <button onClick={handleDownload}>Download as .txt</button>
        </div>
      )}
    </div>
  );
}

export default MealPlannerForm;
